import 'package:cached_network_image/cached_network_image.dart';import 'package:flutter/material.dart';/// 带有缓存的图片Widget ImageCachedUtils(String imageUrl, {double? iheight, double? iwidth}) {  return CachedNetworkImage(    imageUrl: imageUrl,    height: iheight,    width: iwidth,    fit: BoxFit.cover,    /// errorWidget    errorWidget: (      BuildContext context,      String url,      dynamic error,    ) =>        Icon(Icons.error),    /// placeholder    placeholder: (      BuildContext context,      String url,    ) =>        Container(      color: Colors.grey[200],    ),  );}